<?php/* Display a thankyou notice */add_action('admin_notices', 'at_admin_messages');function at_admin_messages() {	global $current_user ;        $user_id = $current_user->ID;		delete_user_meta($user_id, 'at_iavcp_ignore_notice');				if ( isset($_GET['at_wpa'])) {             update_option( 'at_wpatt_option_enable', '0' );		}		$get_at_wpatt_option_enable = get_option('at_wpatt_option_enable');	if ( $get_at_wpatt_option_enable == '1') {		echo '<div class="error"><p>';         printf(__('ATTENZIONE!!!<br/>Il plugin WP ATTACHMENTS è stato rimosso da Amministrazione Trasparente! Per continuare ad utilizzarlo, è necessario installare WP ATTACHMENTS da Plugins -> Aggiungi Nuovo!<br/><b><a href="%1$s">Nascondi questo avviso</a></b>'), '?at_wpa');        echo "</p></div>";	}		$get_at_option_id = get_option('at_option_id');	if ($get_at_option_id == '0') { /* Se  non è stato compilato l'url per "Torna al Sommario", informiamo brutalmente l'admin fino a quando non provvede :-) */		echo '<div class="error"><p>'; 		printf(__('Per iniziare devi inserire in <b>Trasparenza > Impostazioni </b> l\'id della pagina che intendi usare come indice di Amministrazione Trasparente (dove devono essere inseriti i tag)'));		echo "</p></div>";	}}?>